var filmNb = 400; // The number of films in filmList

function SelectActor(filmName){
	var actorsList =[]; // The list of actors for this film
	var actorNb=0; // The number of actor for this film
	var actorsString = filmList[filmName].actors;
	var actorsStringLength = actorsString.length;
	var actualActor = "";
	for(var i=0;i<actorsStringLength;i++){
		if (actorsString[i]!=","){
			actualActor=actualActor+actorsString[i];
		}
		else {
			actorsList.push(actualActor);
			actorNb+=1;
			actualActor="";
		}
	}
	actorNb+=1;
	actorsList.push(actualActor);
	var chosenActorIndex=Math.floor(Math.random()*actorNb);
	return actorsList[chosenActorIndex];
}

function SelectDifferentActor(filmIndex){
	var differentIndexFilm;
	var filmActors = filmList["Film"+filmIndex].actors;
	var differentActor;
	do {
		differentIndexFilm=Math.floor(Math.random()*filmNb)+1;
		differentActor=SelectActor("Film"+differentIndexFilm);
	} while (filmActors.includes(differentActor) || differentIndexFilm == filmIndex)		
	return differentActor;
}

function SelectDifferentFilm(actor,filmIndex){
	var differentIndexFilm;
	var filmActors;
	do {
		differentIndexFilm=Math.floor(Math.random()*filmNb)+1;
		filmActors = filmList["Film"+differentIndexFilm].actors;
	} while (filmActors.includes(actor) || differentIndexFilm == filmIndex)
	return filmList["Film"+differentIndexFilm].title;
}

