function addClass(obj, cls){
	var obj_class = obj.className,//Get class content.
		blank = (obj_class != '') ? ' ' : '';//Determine whether the obtained class is empty. If it is not empty, add a 'space' in front..
	added = obj_class + blank + cls;//Combine the original class with the class you need to add..
	obj.className = added;//Replace the original class.
}

function removeClass(obj, cls){
	var obj_class = ' '+obj.className+' ';//Get the class content, and add a space at the beginning and the end. ex) 'abc    bcd' -> ' abc    bcd '
	obj_class = obj_class.replace(/(\s+)/gi, ' '),//Replace extra null characters with a space. ex) ' abc    bcd ' -> ' abc bcd '
		removed = obj_class.replace(' '+cls+' ', ' ');//Replace the original class with spaces before the end class. ex) ' abc bcd ' -> 'bcd '
	removed = removed.replace(/(^\s+)|(\s+$)/g, '');//Remove the leading and trailing spaces. ex) 'bcd ' -> 'bcd'
	obj.className = removed;//Replace the original class.
}

function hasClass(obj, cls){
	var obj_class = obj.className,//get the content of class .
		obj_class_lst = obj_class.split(/\s+/);//by using split Null character to make cls turn to array.
	x = 0;
	for(x in obj_class_lst) {
		if(obj_class_lst[x] == cls) {//Loop array, determine whether to include
			return true;
		}
	}
	return false;
}

