function submit(score, time)
{
	console.log("Submit !!!");
	var XHR = new XMLHttpRequest();
	var urlEncodedData = "";
	var urlEncodedDataPairs = [];

	urlEncodedDataPairs.push(encodeURIComponent('score') + '=' + encodeURIComponent(score));
	urlEncodedDataPairs.push(encodeURIComponent('time') + '=' + encodeURIComponent(time));
	urlEncodedData = urlEncodedDataPairs.join('&').replace(/%20/g, '+');
	
	// define the method and the target
	XHR.open('POST', 'inscripSession.php');
	XHR.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded');

	// Send the data
	XHR.send(urlEncodedData);
	document.location.href="inscripScore.php";
}

